INSERT INTO public.job_postings (title, slug, description, short_description, job_type, category, status, location, fylke, kommune, region, company_name, vessel_name, salary_min, salary_max, salary_text, start_date, application_deadline, published_at, contact_email, contact_phone, meta_title, meta_description) VALUES
('Matros - Servicefartøy', 'matros-servicefartoy', 'Bluecrew rekrutterer løpende matroser til servicefartøy for våre kunder innen havbruk og maritim sektor. Krav: Gyldig STCW-sertifikater, D5/D6 sertifikat ønskelig, erfaring fra servicefartøy eller havbruk. Vi tilbyr konkurransedyktig lønn, varierte oppdrag langs kysten og fleksible rotasjonsordninger. Dette er løpende rekruttering - vi kontakter deg når relevante oppdrag dukker opp.', 'Løpende rekruttering av matroser til servicefartøy.', 'Fast', 'Dekk', 'active', 'Vestland / Trøndelag', 'Vestland', 'Bergen', 'Vestlandet', NULL, NULL, 620000, 750000, 'Konkurransedyktig lønn', NULL, '2026-12-31', NOW(), 'post@bluecrew.no', '+47 77 02 90 00', 'Matros Servicefartøy | Bluecrew', 'Søk stilling som matros på servicefartøy.');
INSERT INTO public.job_postings (title, slug, description, short_description, job_type, category, status, location, fylke, kommune, region, company_name, vessel_name, salary_min, salary_max, salary_text, start_date, application_deadline, published_at, contact_email, contact_phone, meta_title, meta_description) VALUES
('Matros - Subsea/Offshore', 'matros-subsea-offshore', 'Bluecrew søker kontinuerlig etter matroser til subsea- og offshore-operasjoner. Krav: Gyldig STCW-sertifikater, offshore sikkerhetskurs (BOSIET/HUET), minimum 1 års erfaring fra offshore og god fysikk. Vi tilbyr konkurransedyktig lønn og 2-2-2-4 eller lignende turnus. Løpende rekruttering.', 'Løpende rekruttering av matroser til subsea og offshore.', 'Fast', 'Dekk', 'active', 'Stavanger / Bergen', 'Rogaland', 'Stavanger', 'Sørlandet', NULL, NULL, 700000, 850000, 'Konkurransedyktig lønn', NULL, '2026-12-31', NOW(), 'post@bluecrew.no', '+47 77 02 90 00', 'Matros Subsea Offshore | Bluecrew', 'Søk stilling som matros innen subsea og offshore.');
INSERT INTO public.job_postings (title, slug, description, short_description, job_type, category, status, location, fylke, kommune, region, company_name, vessel_name, salary_min, salary_max, salary_text, start_date, application_deadline, published_at, contact_email, contact_phone, meta_title, meta_description) VALUES
('Styrmann - Kystfart', 'styrmann-kystfart', 'Vi rekrutterer løpende styrmann til kystfart og servicefartøy. Kvalifikasjoner: D4 eller D5 sertifikat, GOC, erfaring fra servicefartøy eller kystfart. Vi tilbyr attraktiv lønn og varierte oppdrag langs norskekysten. Registrer deg for løpende vurdering.', 'Løpende rekruttering av styrmann til kystfart.', 'Fast', 'Dekk', 'active', 'Hele kysten', 'Vestland', 'Bergen', 'Vestlandet', NULL, NULL, 750000, 900000, 'Konkurransedyktig lønn', NULL, '2026-12-31', NOW(), 'post@bluecrew.no', '+47 77 02 90 00', 'Styrmann Kystfart | Bluecrew', 'Søk stilling som styrmann i kystfart.');
INSERT INTO public.job_postings (title, slug, description, short_description, job_type, category, status, location, fylke, kommune, region, company_name, vessel_name, salary_min, salary_max, salary_text, start_date, application_deadline, published_at, contact_email, contact_phone, meta_title, meta_description) VALUES
('Overstyrmann - Offshore', 'overstyrmann-offshore', 'Bluecrew søker erfarne overstyrmenn til offshore-operasjoner. Krav: D3 sertifikat eller høyere, DP sertifikat (Unlimited foretrukket), offshore sikkerhetssertifikater, erfaring fra PSV, AHTS eller CSV. Vi tilbyr høy lønn og attraktive rotasjonsordninger.', 'Overstyrmann søkes til offshore. DP-erfaring.', 'Fast', 'Dekk', 'active', 'Stavanger / Bergen', 'Rogaland', 'Stavanger', 'Sørlandet', NULL, NULL, 850000, 1050000, 'Konkurransedyktig lønn', NULL, '2026-12-31', NOW(), 'post@bluecrew.no', '+47 77 02 90 00', 'Overstyrmann Offshore | Bluecrew', 'Søk stilling som overstyrmann offshore.');
